const User=require("../models/User"),Address=require("../models/Address");module.exports={async index(req,res){const{user_id:user_id}=req.params,user=await User.findByPk(user_id,{include:{association:"addresses"}});return res.json(user.addresses)},async store(req,res){const{user_id:user_id}=req.params,{zipcode:zipcode,street:street,number:number}=req.body,user=await User.findByPk(user_id);if(!user)return res.status(400).json({error:"User not Found!"});const address=await Address.create({zipcode:zipcode,street:street,number:number,user_id:user_id});return res.json(address)}};